import React, { useState } from "react";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useBookContext } from "../../BookContext"; // Import the useBookContext hook

const BookList = () => {
  const { bookData } = useBookContext();
  const [sortBy, setSortBy] = useState("none");
  const [filteredBooks, setFilteredBooks] = useState(bookData);

  const handleSortByChange = (event) => {
    const selectedValue = event.target.value;
    setSortBy(selectedValue);

    if (selectedValue === "none") {
      setFilteredBooks(bookData);
    } else if (selectedValue === "genre") {
      const sortedBooks = [...bookData].sort((a, b) =>
        a.volumeInfo.categories[0].localeCompare(b.volumeInfo.categories[0])
      );
      setFilteredBooks(sortedBooks);
    } else if (selectedValue === "author") {
      const sortedBooks = [...bookData].sort((a, b) =>
        a.volumeInfo.authors[0].localeCompare(b.volumeInfo.authors[0])
      );
      setFilteredBooks(sortedBooks);
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Bookstore
      </Typography>
      <FormControl variant="outlined" style={{ marginBottom: "20px" }}>
        <InputLabel>Sort By</InputLabel>
        <Select value={sortBy} onChange={handleSortByChange} label="Sort By">
          <MenuItem value="none">None</MenuItem>
          <MenuItem value="genre">Genre</MenuItem>
          <MenuItem value="author">Author</MenuItem>
        </Select>
      </FormControl>
      <Grid container spacing={3}>
        {filteredBooks.map((book) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={book.id}>
            <Card variant="outlined">
              <CardContent>
                <img
                  src={book.volumeInfo.imageLinks.thumbnail}
                  alt={book.volumeInfo.title}
                />
                <Typography variant="h6" component="div">
                  {book.volumeInfo.title}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Author: {book.volumeInfo.authors[0]}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Genre: {book.volumeInfo.categories[0]}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default BookList;
