import React, { useState, useEffect } from "react";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  Box,
} from "@mui/material";

import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import useGoogleBooks from "../useGoogleBooks"; // Adjust the path to your custom hook file
import { useBookContext } from "../../BookContext";

const BookList = () => {
  //   const { bookData } = useBookContext();
  const bookData = useGoogleBooks();
  console.log("Koca: Book list bookData ", bookData);
  const [selectedAuthor, setSelectedAuthor] = useState("");
  const [filteredBooks, setFilteredBooks] = useState([]);

  const authorNames =
    bookData &&
    Array.isArray(bookData) &&
    bookData.map((book) => {
      const validation =
        book.volumeInfo &&
        book.volumeInfo.authors &&
        book.volumeInfo.authors[0];
    });
  console.log("Koca: authorNames ", authorNames);

  const handleAuthorChange = (event) => {
    const author = event.target.value;
    setSelectedAuthor(author);
    const filteredBooks = bookData.filter(
      (book) => book.volumeInfo.authors[0] === author
    );
    setFilteredBooks(filteredBooks);
  };

  return (
    <Box style={{ display: "flex" }}>
      <Box style={{ marginRight: "20px", paddingTop: "20px" }}>
        <FormControl variant="outlined" style={{ marginBottom: "20px" }}>
          <InputLabel>Select Author</InputLabel>
          <Select
            value={selectedAuthor}
            onChange={handleAuthorChange}
            label="Select Author"
          >
            <MenuItem value="">
              <em>All Authors</em>
            </MenuItem>
            {authorNames.map((author, index) => (
              <MenuItem key={index} value={author}>
                {author}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Box>
      <Box>
        <Box>
          <List>
            {filteredBooks.length > 0 ? (
              filteredBooks.map((book, index) => (
                <ListItem
                  key={index}
                  style={{
                    border: "1px solid #ccc",
                    margin: "5px",
                    padding: "10px",
                  }}
                >
                  <ListItemAvatar>
                    <Avatar
                      alt={book.volumeInfo.title}
                      src={book.volumeInfo.imageLinks.smallThumbnail}
                    />
                  </ListItemAvatar>
                  <ListItemText
                    primary={book.volumeInfo.title}
                    secondary={
                      <React.Fragment>
                        <Typography
                          sx={{ display: "inline" }}
                          component="span"
                          variant="body2"
                          color="text.primary"
                        >
                          {book.volumeInfo.authors[0]}
                        </Typography>{" "}
                        {book.volumeInfo.subtitle}
                      </React.Fragment>
                    }
                  />
                </ListItem>
              ))
            ) : (
              <p>No books found for the selected author.</p>
            )}
          </List>
        </Box>
      </Box>
    </Box>
  );
};

export default BookList;
