import React, { useState, useEffect } from "react";
import Card from "./Card";
import Books from "./Books/Books";
import axios from "axios";
import { useBookContext } from "../BookContext"; // Import the useBookContext hook

const Main = () => {
  const [search, setSearch] = useState("");
  const { bookData, setBookData } = useBookContext(); // Use the context hook to access bookData

  useEffect(() => {
    useEffect(() => {
      const fetchBooks = async () => {
        const books = [];

        for (
          let startIndex = 0;
          startIndex < TOTAL_RESULTS;
          startIndex += RESULTS_PER_PAGE
        ) {
          try {
            const response = await axios.get(
              `${BASE_URL}?q=${SEARCH_TERM}&key=${API_KEY}&startIndex=${startIndex}&maxResults=${RESULTS_PER_PAGE}`
            );

            books.push(...response.data.items);
          } catch (err) {
            console.log(err);
          }
        }

        setBookData(books);
      };

      fetchBooks();
    }, []);
  }, []);

  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=+${search}+&key=AIzaSyBC7KYs-iOijtXP6BiCoe1PjzDveVNNap8`
        )
        .then((res) => setBookData(res.data.items)) // Update context state
        .catch((err) => console.log(err));
    }
  };
  console.log("shared:bookData: ", bookData);
  return (
    <>
      <div className="header">
        <div className="row1">
          <h1>
            A room without books is like
            <br /> a body without a soul.
          </h1>
        </div>
        <div className="row2">
          <h2>Find Your Book</h2>
          <div className="search">
            <input
              type="text"
              placeholder="Enter Your Book Name"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyPress={searchBook}
            />
            <button>
              <i className="fas fa-search"></i>
            </button>
          </div>
          <img src="./images/bg2.png" alt="" />
        </div>
      </div>

      <div className="container">{<Card book={bookData} />}</div>
    </>
  );
};

export default Main;
