import React, { useState, useEffect } from "react";
import Card from "./Card/Card";
import Books from "./Books/Books";
import axios from "axios";
import { useBookContext } from "../BookContext";

const Main = () => {
  const [search, setSearch] = useState("");
  const { bookData, setBookData } = useBookContext();

  useEffect(() => {
    const fetchBooks = async () => {
      let items = [];
      for (let startIndex = 0; startIndex < 40; startIndex += 10) {
        try {
          const response = await axios.get(
            `https://www.googleapis.com/books/v1/volumes?q=computer&startIndex=${startIndex}&maxResults=10&key=AIzaSyBC7KYs-iOijtXP6BiCoe1PjzDveVNNap8`
          );
          if (response.data.items) {
            items = items.concat(response.data.items);
          }
        } catch (err) {}
      }
      setBookData(items);
    };

    fetchBooks();
  }, []);

  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=+${search}+&key=AIzaSyBC7KYs-iOijtXP6BiCoe1PjzDveVNNap8`
        )
        .then((res) => setBookData(res.data.items)) // Update context state
        .catch((err) => console.log(err));
    }
  };

  return (
    <>
      <div className="header">
        <div className="row1">
          <h1>
            A room without books is like
            <br /> a body without a soul.
          </h1>
        </div>
      </div>
      <div className="row2">
        <h2 style={{ color: "black" }}>Find Your Book</h2>
        <div className="search">
          <input
            type="text"
            placeholder="Enter Your Book Name"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyPress={searchBook}
          />
          <button>
            <i className="fas fa-search"></i>
          </button>
        </div>
        <img src="./images/bg2.png" alt="" />
      </div>
      <div className="container">
        <Card book={bookData} />
      </div>
    </>
  );
};

export default Main;
