import React, { useState, useEffect } from "react";
import Card from "./Card";
import Books from "./Books/Books";
import axios from "axios";
import { useBookContext } from "../BookContext"; // Import the useBookContext hook

const Main = () => {
  const [search, setSearch] = useState("");
  const { bookData, setBookData } = useBookContext(); // Use the context hook to access bookData

  useEffect(() => {
    const fetchBooks = async () => {
      let items = [];
      for (let startIndex = 0; startIndex < 40; startIndex += 10) {
        try {
          const response = await axios.get(
            `https://www.googleapis.com/books/v1/volumes?q=computer&startIndex=${startIndex}&maxResults=10&key=AIzaSyBC7KYs-iOijtXP6BiCoe1PjzDveVNNap8`
          );
          if (response.data.items) {
            items = items.concat(response.data.items);
            console.log("Fetched data:", items); // Add this line to log the data
          }
        } catch (err) {
          console.log("Error fetching data:", err); // Add this line to log errors
        }
      }
      setBookData(items);
      console.log("Data set in context:", items); // Add this line to check data setting
    };

    fetchBooks();
  }, []);

  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=+${search}+&key=AIzaSyBC7KYs-iOijtXP6BiCoe1PjzDveVNNap8`
        )
        .then((res) => setBookData(res.data.items)) // Update context state
        .catch((err) => console.log(err));
    }
  };

  return (
    <>
      <div className="header">
        <div className="row1">
          <h1>
            A room without books is like
            <br /> a body without a soul.
          </h1>
        </div>
        <div className="row2">
          <h2>Find Your Book</h2>
          <div className="search">
            <input
              type="text"
              placeholder="Enter Your Book Name"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyPress={searchBook}
            />
            <button>
              <i className="fas fa-search"></i>
            </button>
          </div>
          <img src="./images/bg2.png" alt="" />
        </div>
      </div>

      <div className="container">{<Card book={bookData} />}</div>
    </>
  );
};

export default Main;
