import React, { useEffect, useState } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";

const Cart = ({ cartItems, removeFromCart }) => {
  const [cartData, setCartData] = useState();
  // Use the useEffect hook to load cart data from localStorage when the component mounts
  useEffect(() => {
    const cartData = localStorage.getItem("cart");
    if (cartData) {
      const parsedCartData = JSON.parse(cartData);
      setCartData(parsedCartData);
      // console.log("Koca: parsedCartData ", parsedCartData);
      // Update the cartItems with the data from localStorage
      // This is useful if the user revisits the cart page, and you want to display the saved cart items
      // You can also add more logic here if needed
    }
  }, []);

  return (
    <div>
      {cartItems.map((item, index) => (
        <Card key={index}>
          <CardMedia
            component="img"
            height="140"
            image={item.volumeInfo.imageLinks.smallThumbnail}
            alt=""
          />
          <CardContent>
            <Typography variant="h6" component="div">
              {item.volumeInfo.title}
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              PRICE: &#8377;{item.saleInfo.listPrice.amount}
            </Typography>
            <Button
              size="large"
              variant="contained"
              onClick={() => removeFromCart(index)}
            >
              Remove from Cart
            </Button>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default Cart;
