import React, { useState, useEffect } from "react";
import {
  Typography,
  Grid,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useBookContext } from "../../BookContext";

const BookList = () => {
  const { bookData } = useBookContext();

  const [sortBy, setSortBy] = useState("none");
  const [filteredBooks, setFilteredBooks] = useState(bookData.items);

  const handleSortByChange = (event) => {
    const selectedValue = event.target.value;
    setSortBy(selectedValue);

    if (selectedValue === "none") {
      setFilteredBooks(bookData.items);
    } else if (selectedValue === "genre") {
      const sortedBooks = [...bookData.items].sort((a, b) =>
        a.volumeInfo.categories[0].localeCompare(b.volumeInfo.categories[0])
      );
      setFilteredBooks(sortedBooks);
    } else if (selectedValue === "author") {
      const sortedBooks = [...bookData.items].sort((a, b) =>
        a.volumeInfo.authors[0].localeCompare(b.volumeInfo.authors[0])
      );
      setFilteredBooks(sortedBooks);
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Bookstore
      </Typography>
      <FormControl variant="outlined" style={{ marginBottom: "20px" }}>
        <InputLabel>Sort By</InputLabel>
        <Select value={sortBy} onChange={handleSortByChange} label="Sort By">
          <MenuItem value="none">None</MenuItem>
          <MenuItem value="genre">Genre</MenuItem>
          <MenuItem value="author">Author</MenuItem>
        </Select>
      </FormControl>
      <ul>
        {filteredBooks.map((book) => (
          <li key={book.id}>
            <img
              src={book.volumeInfo.imageLinks.thumbnail}
              alt={book.volumeInfo.title}
            />
            <h6>{book.volumeInfo.title}</h6>
            <p>Author: {book.volumeInfo.authors[0]}</p>
            <p>Genre: {book.volumeInfo.categories[0]}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookList;
