import React, { useState, useEffect } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import "./style.css";
import axios from "axios";
import { useBookContext } from "../../BookContext";

const MainCard = () => {
  const { bookData, setBookData } = useBookContext();

  const [cart, setCart] = useState([]);
  const [search, setSearch] = useState("");
  useEffect(() => {
    const cartData = localStorage.getItem("cart");
    if (cartData) {
      setCart(JSON.parse(cartData));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=+${search}+&key=AIzaSyBC7KYs-iOijtXP6BiCoe1PjzDveVNNap8`
        )
        .then((res) => setBookData(res.data.items))
        .catch((err) => console.log(err));
    }
  };

  return (
    <>
      <div className="row2">
        <div className="search">
          <input
            type="text"
            placeholder="Enter Your Book Name"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyPress={searchBook}
          />
          <button>
            <i className="fas fa-search"></i>
          </button>
        </div>
      </div>

      <div className="card-container">
        {bookData &&
          bookData.map((item, index) => {
            let thumbnail =
              item.volumeInfo.imageLinks &&
              item.volumeInfo.imageLinks.smallThumbnail;
            let amount =
              item.saleInfo.listPrice && item.saleInfo.listPrice.amount;
            if (thumbnail != undefined && amount != undefined) {
              return (
                <Card key={index}>
                  <CardMedia
                    component="img"
                    height="140"
                    image={thumbnail}
                    alt=""
                  />
                  <CardContent>
                    <Typography variant="h6" component="div">
                      {item.volumeInfo.title}
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                      PRICE: &#8377;{amount}
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                      AUTHOR: {item.volumeInfo.authors[0]}
                    </Typography>
                    <Button
                      size="large"
                      variant="contained"
                      onClick={() => addToCart(item)}
                    >
                      ADD TO CART
                    </Button>
                  </CardContent>
                </Card>
              );
            }
          })}
      </div>

      {bookData.map((book, index) => (
        <Grid item xs={12} sm={6} md={3} key={index}>
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={
                book.volumeInfo.imageLinks &&
                book.volumeInfo.imageLinks.smallThumbnail
              }
              alt=""
            />
            <CardHeader
              title={book.volumeInfo.title}
              subheader={`Price : ${
                book.saleInfo.listPrice && book.saleInfo.listPrice.amount
              }`}
            />
            <CardContent>
              <Typography variant="h5" gutterBottom>
                AUTHOR:{" "}
                {book.volumeInfo &&
                  book.volumeInfo.authors &&
                  book.volumeInfo.authors[0]}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </>
  );
};

export default MainCard;
