import React, { useState } from "react";
import Main from "./Components/Main";
import "./Components/style.css";
import Footer from "./Components/Footer/Footer";
import Cart from "./Components/cart/AddToCart";
import ResponsiveAppBar from "./Components/Navbar/Navbar";
import Books from "./Components/Books/Books"; // Import your Books component
import Authors from "./Components/Author";
function App() {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (item) => {
    setCartItems([
      ...cartItems,
      { title: item.volumeInfo.title, price: item.saleInfo.listPrice.amount },
    ]);
  };

  const removeItem = (index) => {
    const newCartItems = [...cartItems];
    newCartItems.splice(index, 1);
    setCartItems(newCartItems);
  };
  return (
    <>
      <ResponsiveAppBar />
      <Main addToCart={addToCart} />
      <Cart cartItems={cartItems} removeItem={removeItem} />
      <Footer />
    </>
  );
}

export default App;
