// AuthorList.js
import React from "react";
import { Link } from "react-router-dom";
import { Card, CardContent, Typography } from "@mui/material";
import useGoogleBooks from "../useGoogleBooks";

const authors = ["David M. Beazley"]; // Replace with your list of authors

const AuthorList = () => {
  const bookData = useGoogleBooks();

  const authorNames =
    bookData &&
    Array.isArray(bookData) &&
    bookData.map(
      (book) =>
        book.volumeInfo && book.volumeInfo.authors && book.volumeInfo.authors[0]
    );

  const authorCounts = {};

  for (const book of bookData) {
    const authors = book.volumeInfo.authors;
    const bookTitle = book.volumeInfo.title;

    if (authors) {
      // Iterate through authors of the current book
      for (const author of authors) {
        // Increment the count for the author or initialize it to 1
        authorCounts[author] = authorCounts[author] || { count: 0, books: [] };
        authorCounts[author].count += 1;
        authorCounts[author].books.push(bookTitle);
      }
    }
  }
  for (const author in authorCounts) {
    console.log(`${author} has written ${authorCounts[author].count} book(s):`);
    for (const book of authorCounts[author].books) {
      console.log(`- ${book}`);
    }
    console.log();
  }
  return (
    <div>
      {authorNames.map((author, index) => (
        <Card key={index} style={{ margin: "10px" }}>
          <CardContent>
            <Typography variant="h6" component="div">
              <Link to={`/author/${author}`}>{author}</Link>
            </Typography>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default AuthorList;
