import React, { useEffect, useState } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";

const Cart = ({ cartItems, removeFromCart }) => {
  const [cartData, setCartData] = useState();
  console.log("Koca: cartData ", cartData);
  useEffect(() => {
    const cartData = localStorage.getItem("cart");
    if (cartData) {
      const parsedCartData = JSON.parse(cartData);
      console.log("Koca: parsedCartData ", parsedCartData);
      setCartData(parsedCartData);
    }
  }, []);

  return (
    <div>
      {cartData.map((book, index) => (
        <Card key={index}>
          <CardMedia
            component="img"
            height="140"
            image={
              book?.volumeInfo && book?.volumeInfo?.imageLinks?.smallThumbnail
            }
            alt=""
          />
          <CardContent>
            <Typography variant="h6" component="div">
              {book?.volumeInfo?.title}
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              PRICE: &#8377;{" "}
              {book?.saleInfo.listPrice && book?.saleInfo?.listPrice?.amount}
            </Typography>
            <Button
              size="large"
              variant="contained"
              onClick={() => removeFromCart(index)}
            >
              Remove from Cart
            </Button>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default Cart;
